import React from 'react';
import { Box, Typography, Button, IconButton, Container } from '@mui/material';
import GitHubIcon from '@mui/icons-material/GitHub';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import TelegramIcon from '@mui/icons-material/Telegram';
import TypewriterText from '../components/TypewriterText';
import TechIconsPanel from '../components/TechIconsPanel';

const Home: React.FC = () => {
  const socialLinks = [
    { icon: <GitHubIcon />, url: 'https://github.com/dolkons' },
    { icon: <LinkedInIcon />, url: 'https://linkedin.com/in/dolkons' },
    { icon: <TelegramIcon />, url: 'https://t.me/dolkons' },
  ];

  return (
    <Container maxWidth="md">
      <TechIconsPanel />
      <Box
        sx={{
          minHeight: '100vh',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          gap: 4,
          position: 'relative',
          zIndex: 1,
        }}
      >
        <Box
          component="img"
          src="/avatar.jpg"
          alt="Konstantin Dolgii"
          sx={{
            width: { xs: 150, sm: 200 },
            height: { xs: 150, sm: 200 },
            borderRadius: '50%',
            objectFit: 'cover',
            border: '4px solid',
            borderColor: 'primary.main',
            boxShadow: '0 0 20px rgba(144, 202, 249, 0.3)',
            marginTop: -8,
          }}
        />
        <Box
          sx={{
            textAlign: 'center',
            maxWidth: '600px',
            backgroundColor: 'rgba(30, 30, 30, 0.7)',
            backdropFilter: 'blur(10px)',
            padding: 4,
            borderRadius: 4,
            boxShadow: '0 4px 30px rgba(0, 0, 0, 0.1)',
          }}
        >
          <TypewriterText
            text="Привет! Я Константин Долгий, QA Automation Engineer"
            sx={{
              fontSize: { xs: '1.5rem', sm: '2rem' },
              fontWeight: 500,
              color: 'primary.main',
              marginBottom: 2,
            }}
          />
          <Typography
            variant="body1"
            sx={{
              fontSize: { xs: '1rem', sm: '1.2rem' },
              color: 'text.secondary',
              marginBottom: 3,
            }}
          >
            Специализируюсь на автоматизации тестирования с использованием JavaScript/TypeScript, Playwright и Docker
          </Typography>
          <Button
            variant="contained"
            color="primary"
            size="large"
            href="/resume.pdf"
            target="_blank"
            sx={{
              padding: '12px 32px',
              fontSize: '1.1rem',
              marginBottom: 4,
              background: 'linear-gradient(45deg, #90caf9 30%, #64b5f6 90%)',
              boxShadow: '0 3px 5px 2px rgba(144, 202, 249, .3)',
              '&:hover': {
                background: 'linear-gradient(45deg, #64b5f6 30%, #90caf9 90%)',
              },
            }}
          >
            Скачать резюме
          </Button>
          <Box
            sx={{
              display: 'flex',
              gap: 2,
              justifyContent: 'center',
            }}
          >
            {socialLinks.map((link, index) => (
              <IconButton
                key={index}
                href={link.url}
                target="_blank"
                sx={{
                  color: 'primary.main',
                  transition: 'transform 0.2s',
                  '&:hover': {
                    transform: 'scale(1.2)',
                    color: 'primary.light',
                  },
                }}
              >
                {link.icon}
              </IconButton>
            ))}
          </Box>
        </Box>
      </Box>
    </Container>
  );
};

export default Home;
