import React from 'react';
import { Box, useTheme, useMediaQuery } from '@mui/material';
import JavaScriptIcon from '@mui/icons-material/Code';
import TypeScriptIcon from '@mui/icons-material/Code';
import PlaywrightIcon from '@mui/icons-material/BugReport';
import DockerIcon from '@mui/icons-material/Storage';
import ReactIcon from '@mui/icons-material/Code';
import NodeIcon from '@mui/icons-material/Code';
import GitIcon from '@mui/icons-material/Code';
import JestIcon from '@mui/icons-material/Code';

const TechIconsPanel: React.FC = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const techIcons = [
    { icon: <JavaScriptIcon />, label: 'JavaScript' },
    { icon: <TypeScriptIcon />, label: 'TypeScript' },
    { icon: <PlaywrightIcon />, label: 'Playwright' },
    { icon: <DockerIcon />, label: 'Docker' },
    { icon: <ReactIcon />, label: 'React' },
    { icon: <NodeIcon />, label: 'Node.js' },
    { icon: <GitIcon />, label: 'Git' },
    { icon: <JestIcon />, label: 'Jest' },
  ];

  return (
    <Box
      sx={{
        position: 'fixed',
        left: { xs: 10, sm: 60 },
        top: '50%',
        transform: 'translateY(-50%)',
        height: { xs: '40vh', sm: '60vh' },
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        gap: { xs: 2, sm: 4 },
        zIndex: 1000,
        overflow: 'hidden',
        '&::before, &::after': {
          content: '""',
          position: 'absolute',
          left: 0,
          right: 0,
          height: { xs: '50px', sm: '100px' },
          zIndex: 2,
          pointerEvents: 'none',
        },
        '&::before': {
          top: 0,
          background: 'linear-gradient(to bottom, rgba(255,255,255,1) 0%, rgba(255,255,255,0) 100%)',
        },
        '&::after': {
          bottom: 0,
          background: 'linear-gradient(to top, rgba(255,255,255,1) 0%, rgba(255,255,255,0) 100%)',
        }
      }}
    >
      <Box
        sx={{
          animation: 'slideDown 20s linear infinite',
          '@keyframes slideDown': {
            '0%': {
              transform: 'translateY(-100%)',
            },
            '100%': {
              transform: 'translateY(100%)',
            },
          },
        }}
      >
        {techIcons.map((tech, index) => (
          <Box
            key={tech.label}
            sx={{
              display: 'flex',
              alignItems: 'center',
              gap: { xs: 1, sm: 2 },
              backgroundColor: 'rgba(255, 255, 255, 0.8)',
              backdropFilter: 'blur(10px)',
              padding: { xs: '4px 8px', sm: '8px 16px' },
              borderRadius: 2,
              boxShadow: 1,
              transition: 'transform 0.2s',
              marginBottom: { xs: 2, sm: 4 },
              '&:hover': {
                transform: 'scale(1.1)',
                backgroundColor: 'rgba(255, 255, 255, 0.9)',
              },
            }}
          >
            {tech.icon}
            <Box
              component="span"
              sx={{
                fontSize: { xs: '0.7rem', sm: '0.9rem' },
                fontWeight: 500,
                color: 'text.primary',
                display: { xs: 'none', sm: 'block' }
              }}
            >
              {tech.label}
            </Box>
          </Box>
        ))}
      </Box>
    </Box>
  );
};

export default TechIconsPanel;
