import React from 'react';
import { Box, Typography, Avatar, Button, IconButton, useTheme, useMediaQuery } from '@mui/material';
import MainLayout from '../layouts/MainLayout';
import TypewriterText from '../components/TypewriterText';
import TechIconsPanel from '../components/TechIconsPanel';
import GitHubIcon from '@mui/icons-material/GitHub';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import TelegramIcon from '@mui/icons-material/Telegram';
import avatarImage from '../assets/images/avatar.jpg';
import vkIcon from '../assets/icons/vk.svg';

const VkIcon = () => (
  <svg viewBox="0 0 24 24" width="24" height="24" fill="currentColor">
    <path d="M15.07 2H8.93C3.33 2 2 3.33 2 8.93V15.07C2 20.67 3.33 22 8.93 22H15.07C20.67 22 22 20.67 22 15.07V8.93C22 3.33 20.67 2 15.07 2ZM18.15 16.27H16.69C16.14 16.27 15.97 15.97 14.86 14.96C13.86 14.13 13.49 13.88 13.27 13.88C12.88 13.88 12.75 14.04 12.75 14.55V15.69C12.75 16.1 12.59 16.27 11.73 16.27C10.07 16.27 8.64 15.25 7.71 13.64C5.79 10.08 5.39 9.23 5.39 8.77C5.39 8.63 5.46 8.5 5.66 8.5H7.12C7.47 8.5 7.63 8.72 7.79 9.13C8.53 11.05 9.59 12.5 10.04 12.5C10.31 12.5 10.42 12.37 10.42 11.94V10.39C10.37 9.28 10.07 9.21 10.07 8.93C10.07 8.79 10.21 8.67 10.44 8.67H13.27C13.57 8.67 13.68 8.83 13.68 9.16V11.22C13.68 11.58 13.82 11.74 13.89 11.74C14.09 11.74 14.38 11.6 14.86 11.16C15.85 10.17 16.49 8.63 16.49 8.63C16.58 8.45 16.78 8.27 17.07 8.27H18.53C18.73 8.27 18.82 8.45 18.73 8.63C18.65 8.83 17.72 10.59 16.65 12.08C16.07 12.92 15.56 13.45 15.38 13.66C15.07 14 15.17 14.13 15.38 14.42C15.58 14.72 16.35 15.66 16.65 16.08C17.11 16.74 17.59 17.27 17.73 17.47C17.95 17.76 17.86 17.94 17.66 17.94L18.15 16.27Z" />
  </svg>
);

const Home: React.FC = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const socialLinks = [
    { icon: <GitHubIcon />, url: 'https://github.com/dolkons', label: 'GitHub' },
    { icon: <LinkedInIcon />, url: 'https://linkedin.com/in/dolkons', label: 'LinkedIn' },
    { icon: <TelegramIcon />, url: 'https://t.me/dolkons', label: 'Telegram' },
    { icon: <VkIcon />, url: 'https://vk.com/dolkons', label: 'VK' },
  ];

  return (
    <MainLayout>
      <TechIconsPanel />
      <Box 
        display="flex" 
        flexDirection="column" 
        alignItems="center" 
        justifyContent="center"
        gap={6}
        sx={{
          minHeight: '100vh',
          py: { xs: 8, sm: 12 },
          px: { xs: 2, sm: 4 },
          background: 'linear-gradient(180deg, rgba(245,245,245,0.95) 0%, rgba(235,235,235,0.85) 100%)',
          backdropFilter: 'blur(10px)',
          position: 'relative',
          '&::before': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'radial-gradient(circle at 50% 50%, rgba(25, 118, 210, 0.05) 0%, transparent 70%)',
            pointerEvents: 'none',
          }
        }}
      >
        {/* Кнопка резюме */}
        <Button
          variant="contained"
          size="small"
          color="primary"
          sx={{ 
            position: 'fixed',
            top: 20,
            right: 20,
            px: 1.5,
            py: 0.5,
            fontSize: '0.7rem',
            textTransform: 'none',
            borderRadius: 1.5,
            boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
            transition: 'all 0.3s ease-in-out',
            zIndex: 1000,
            backgroundColor: 'rgba(117, 117, 117, 0.8)',
            backdropFilter: 'blur(4px)',
            '&:hover': {
              transform: 'translateY(-2px)',
              boxShadow: '0 4px 12px rgba(0,0,0,0.15)',
              backgroundColor: 'rgba(117, 117, 117, 0.9)',
            }
          }}
          href="/resume.pdf"
          download
        >
          Скачать резюме
        </Button>

        {/* Социальные сети */}
        <Box 
          display="flex" 
          gap={1}
          sx={{
            position: 'fixed',
            top: 60,
            right: 20,
            zIndex: 1000,
            animation: 'slideUp 0.8s ease-out 0.5s both',
            '@keyframes slideUp': {
              '0%': {
                opacity: 0,
                transform: 'translateY(20px)',
              },
              '100%': {
                opacity: 1,
                transform: 'translateY(0)',
              },
            }
          }}
        >
          {socialLinks.map((social) => (
            <IconButton
              key={social.label}
              href={social.url}
              target="_blank"
              rel="noopener noreferrer"
              color="primary"
              sx={{ 
                width: 28,
                height: 28,
                backgroundColor: 'rgba(25, 118, 210, 0.08)',
                transition: 'all 0.3s ease-in-out',
                '&:hover': {
                  backgroundColor: 'rgba(25, 118, 210, 0.15)',
                  transform: 'translateY(-2px)',
                  boxShadow: '0 4px 12px rgba(25, 118, 210, 0.2)',
                },
                '& svg': {
                  fontSize: '1rem'
                }
              }}
              aria-label={social.label}
            >
              {social.icon}
            </IconButton>
          ))}
        </Box>

        {/* Аватар */}
        <Avatar
          src={avatarImage}
          sx={{ 
            width: { xs: 80, sm: 110 }, 
            height: { xs: 80, sm: 110 },
            border: '2px solid',
            borderColor: 'primary.main',
            boxShadow: '0 8px 32px rgba(0,0,0,0.1)',
            mt: { xs: -32, sm: -48 },
            transition: 'all 0.3s ease-in-out',
            '&:hover': {
              transform: 'scale(1.05)',
              boxShadow: '0 12px 40px rgba(0,0,0,0.15)',
            }
          }}
          alt="Костя Долгий"
        />

        {/* Основная информация */}
        <Box 
          textAlign="center" 
          maxWidth="800px"
          sx={{
            animation: 'fadeIn 1s ease-out',
            '@keyframes fadeIn': {
              '0%': {
                opacity: 0,
                transform: 'translateY(20px)',
              },
              '100%': {
                opacity: 1,
                transform: 'translateY(0)',
              },
            }
          }}
        >
          <TypewriterText 
            variant={isMobile ? "h6" : "h5"}
            gutterBottom
            text="Привет, Я Костя"
            delay={100}
            sx={{ 
              mb: 2,
              fontWeight: 400,
              background: 'linear-gradient(45deg, #1976d2, #2196f3)',
              backgroundClip: 'text',
              textFillColor: 'transparent',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
            }}
          />
          <Typography 
            variant="body1" 
            paragraph 
            sx={{ 
              fontSize: { xs: '1rem', sm: '1.1rem' },
              mb: 6,
              color: 'text.secondary',
              lineHeight: 1.8,
              maxWidth: '600px',
              mx: 'auto'
            }}
          >
            Добро пожаловать на мою персональную страничку в глубоком океане Интернета.
          </Typography>
        </Box>
      </Box>
    </MainLayout>
  );
};

export default Home;
