import React from 'react';
import { Box, Typography, Paper, Avatar, Button, IconButton, Container } from '@mui/material';
import MainLayout from '../layouts/MainLayout';
import GitHubIcon from '@mui/icons-material/GitHub';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import TelegramIcon from '@mui/icons-material/Telegram';
import InstagramIcon from '@mui/icons-material/Instagram';

const Home: React.FC = () => {
  const socialLinks = [
    { icon: <GitHubIcon />, url: 'https://github.com/dolkons', label: 'GitHub' },
    { icon: <LinkedInIcon />, url: 'https://linkedin.com/in/dolkons', label: 'LinkedIn' },
    { icon: <TelegramIcon />, url: 'https://t.me/dolkons', label: 'Telegram' },
    { icon: <InstagramIcon />, url: 'https://instagram.com/dolkons1989', label: 'Instagram' },
  ];

  return (
    <MainLayout>
      <Container maxWidth="md">
        <Box display="flex" flexDirection="column" alignItems="center" gap={4}>
          {/* Аватар */}
          <Avatar
            src="/images/avatar.jpg"
            sx={{ 
              width: 200, 
              height: 200,
              border: '4px solid',
              borderColor: 'primary.main',
              boxShadow: 3
            }}
            alt="Костя Долгий"
          />

          {/* Основная информация */}
          <Paper elevation={3} sx={{ p: 4, width: '100%', textAlign: 'center' }}>
            <Typography variant="h4" gutterBottom>
              Привет, Я Костя
            </Typography>
            <Typography variant="h5" color="primary" gutterBottom>
              QA Web Automation Engineer
            </Typography>
            <Typography variant="body1" paragraph>
              Добро пожаловать на мою персональную страничку в глубоком океане Интернета.
              Здесь я делюсь своим опытом в области автоматизации тестирования веб-приложений,
              особенно с использованием Playwright.
            </Typography>
          </Paper>

          {/* Кнопка резюме */}
          <Button
            variant="contained"
            size="large"
            color="primary"
            sx={{ 
              px: 4,
              py: 1.5,
              fontSize: '1.1rem',
              textTransform: 'none'
            }}
            href="/resume.pdf"
            download
          >
            Скачать резюме
          </Button>

          {/* Социальные сети */}
          <Box display="flex" gap={2}>
            {socialLinks.map((social) => (
              <IconButton
                key={social.label}
                href={social.url}
                target="_blank"
                rel="noopener noreferrer"
                color="primary"
                sx={{ 
                  width: 48,
                  height: 48,
                  '&:hover': {
                    backgroundColor: 'primary.light',
                  }
                }}
                aria-label={social.label}
              >
                {social.icon}
              </IconButton>
            ))}
          </Box>
        </Box>
      </Container>
    </MainLayout>
  );
};

export default Home;
