import React from 'react';
import { Box, Paper, Typography } from '@mui/material';
import { MDXProvider } from '@mdx-js/react';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { vscDarkPlus } from 'react-syntax-highlighter/dist/esm/styles/prism';
import type { MDXComponents } from 'mdx/types';

const components: MDXComponents = {
  h1: (props) => <Typography variant="h3" gutterBottom {...props} />,
  h2: (props) => <Typography variant="h4" gutterBottom {...props} />,
  h3: (props) => <Typography variant="h5" gutterBottom {...props} />,
  p: (props) => <Typography variant="body1" paragraph {...props} />,
  code: ({ className, children }) => {
    const language = className?.replace('language-', '') || 'text';
    return (
      <SyntaxHighlighter language={language} style={vscDarkPlus}>
        {String(children)}
      </SyntaxHighlighter>
    );
  },
};

interface MDXArticleProps {
  content: React.ReactNode;
}

const MDXArticle: React.FC<MDXArticleProps> = ({ content }) => {
  return (
    <Paper elevation={3} sx={{ p: 4 }}>
      <MDXProvider components={components}>
        <Box>{content}</Box>
      </MDXProvider>
    </Paper>
  );
};

export default MDXArticle;
